import React, { useState } from 'react';
import ProjectCard from './ProjectCard';
import ProjectDetail from './ProjectDetail'; // Importe o novo componente

// --- IMAGENS DOS PROJETOS ---
// Importe aqui as imagens que você colocou na pasta 'src/assets'
import steamProjectImg from '../assets/pi-steam.jpg';

// --- DADOS DOS PROJETOS ---
const projects = [
  {
    name: "Recriação do Layout da Loja Steam (PI 1º Semestre)",
    description: "Como proposta de trabalho interdisciplinar, o projeto consistiu em recriar fielmente o layout e a identidade visual da página principal da plataforma de jogos Steam. O objetivo principal foi aprofundar os conhecimentos em design de interface (UI), hierarquia visual e composição de elementos gráficos.",
    technologies: "Adobe Photoshop",
    screenshots: [steamProjectImg], // Array com as imagens do projeto
    codeLink: null, // Use null quando não houver link para código
    myRole: "O projeto foi desenvolvido de forma individual. Fui responsável por todas as etapas do processo, desde a análise e decomposição do layout original da Steam até a recriação de cada componente visual, utilizando o Adobe Photoshop para garantir a fidelidade ao design original."
  },
  {
    name: "Projeto Interdisciplinar - 2º Semestre",
    description: "Descrição do projeto desenvolvido no segundo semestre do curso.",
    technologies: "HTML, CSS, JavaScript",
    screenshots: ["https://via.placeholder.com/800x450/4C566A/E5E9F0?text=PI+2º+Semestre"],
    codeLink: "#", // Substitua pelo link real do GitHub
    myRole: "Fui responsável pelo desenvolvimento do front-end e pela estrutura inicial do projeto."
  },
  // Adicione mais projetos aqui...
];

const Portfolio = () => {
  const [selectedProject, setSelectedProject] = useState(null);

  const handleOpenProject = (project) => {
    setSelectedProject(project);
  };

  const handleCloseProject = () => {
    setSelectedProject(null);
  };

  return (
    <section id="portfolio">
      <h2>Projetos Interdisciplinares (PI)</h2>
      <div className="portfolio-grid">
        {projects.map((project, index) => (
          <ProjectCard 
            key={index} 
            project={project} 
            onClick={() => handleOpenProject(project)} 
          />
        ))}
      </div>

      {selectedProject && (
        <ProjectDetail 
          project={selectedProject} 
          onClose={handleCloseProject} 
        />
      )}
    </section>
  );
};

export default Portfolio;
