import React from "react";
import "./Modal.css";

const Modal = ({ project, onClose }) => {
  if (!project) return null;

  // pega a primeira screenshot (se existir)
  const mainImg = project.screenshots && project.screenshots.length > 0
    ? project.screenshots[0]
    : null;

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <button className="modal-close-button" onClick={onClose} aria-label="Fechar">
          &times;
        </button>

        <h2 className="modal-title">{project.name}</h2>

        {mainImg && (
          <div className="modal-image-container">
            <img
              src={mainImg}
              alt={`${project.name} screenshot`}
              className="modal-image"
              onError={(e) => {
                // fallback caso o caminho esteja errado
                e.target.style.display = 'none';
              }}
            />
          </div>
        )}

        <div className="modal-description">
          <h3>Descrição do Projeto</h3>
          <p>{project.description}</p>

          {project.technologies && (
            <>
              <h3>Tecnologias Usadas</h3>
              <p>{project.technologies}</p>
            </>
          )}

          {project.myRole && (
            <>
              <h3>Minha Participação</h3>
              <p>{project.myRole}</p>
            </>
          )}

          {project.codeLink ? (
            <p>
              <a href={project.codeLink} target="_blank" rel="noreferrer">Ver código no GitHub →</a>
            </p>
          ) : (
            <p><em>Não há um repositório de código para este projeto.</em></p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Modal;
